"use strict";(self.webpackChunkblurblah_github_io=self.webpackChunkblurblah_github_io||[]).push([[90691],{15980:(n,e,t)=>{t.r(e),t.d(e,{assets:()=>c,contentTitle:()=>i,default:()=>p,frontMatter:()=>l,metadata:()=>o,toc:()=>a});var r=t(74848),s=t(28453);const l={title:"\uc717 \uc0ac\ub78c\uacfc \uc544\ub7ab \uc0ac\ub78c\uc774 \uc9c0\ucf1c\uc57c \ud558\ub294 \uac83",date:"2010-11-10",categories:["blahblah"],tags:["\uac1c\uc778","\uc0ac\ub78c","\uc870\uc9c1"]},i=void 0,o={permalink:"/2010/11/10/wp/\uc717-\uc0ac\ub78c\uacfc-\uc544\ub7ab-\uc0ac\ub78c\uc774-\uc9c0\ucf1c\uc57c-\ud558\ub294-\uac83",source:"@site/blog/wp/2010-11-10-\uc717-\uc0ac\ub78c\uacfc-\uc544\ub7ab-\uc0ac\ub78c\uc774-\uc9c0\ucf1c\uc57c-\ud558\ub294-\uac83.md",title:"\uc717 \uc0ac\ub78c\uacfc \uc544\ub7ab \uc0ac\ub78c\uc774 \uc9c0\ucf1c\uc57c \ud558\ub294 \uac83",description:"2\ub144 \uc804\uc5d0 \ub3d9\uae30 \ud558\ub098\uac00 \ud1f4\uc0ac\ud55c\ub2e4\uae30\uc5d0 \uc774\ubcc4\ud558\ub294 \uc790\ub9ac\ub97c \uc870\ucd10\ud558\uac8c \ub9cc\ub4e4\uc5c8\ub358 \uc801\uc774 \uc788\ub2e4. \uadf8 \uce5c\uad6c\uac00 \ud68c\uc0ac\ub97c \ub5a0\ub098\ub294 \uc774\uc720\ub294 \uc5ec\ub7ec\uac00\uc9c0\uac00 \uc788\uc5c8\uc9c0\ub9cc \ubb34\ucc99 \ud798\ub4e4\uc5b4 \ud588\ub294\ub370 \ub204\uad6c\ub098 \uadf8\ub807\ub4ef\uc774 \uac00\uc7a5 \ud070 \uc774\uc720\ub294 \uc870\uc9c1\uacfc \uc0ac\ub78c\uc5d0 \ub300\ud55c \uc5b4\ub824\uc6c0\uc774\uc5c8\ub358 \uac78\ub85c \uae30\uc5b5\ub41c\ub2e4. \ub098 \uc5ed\uc2dc \uc870\uc9c1\uacfc \uc0ac\ub78c\ub54c\ubb38\uc5d0 \uc5b4\ub835\uace0 \ud798\ub4e4\uc5c8\uace0 \uc9c0\uae08\uae4c\uc9c0\ub3c4 \uc5b4\ub835\ub2e4\uace0 \uc0dd\uac01\ud558\ub294 \ubd80\ubd84\uc774\uc9c0\ub9cc \ub0a0 \uc544\uc9c1 \uc774\uacf3\uc5d0 \ubd99\uc7a1\uc544\ub450\uace0 \uc788\ub294 \uc774\uc720 \uc5ed\uc2dc \uc870\uc9c1\uc774\ub77c\ub294\uac74 \ucc38 \uc544\uc774\ub7ec\ub2c8\ud55c \uc77c\uc774\ub2e4.",date:"2010-11-10T00:00:00.000Z",formattedDate:"November 10, 2010",tags:[{label:"\uac1c\uc778",permalink:"/tags/\uac1c\uc778"},{label:"\uc0ac\ub78c",permalink:"/tags/\uc0ac\ub78c"},{label:"\uc870\uc9c1",permalink:"/tags/\uc870\uc9c1"}],readingTime:16.93,hasTruncateMarker:!1,authors:[],frontMatter:{title:"\uc717 \uc0ac\ub78c\uacfc \uc544\ub7ab \uc0ac\ub78c\uc774 \uc9c0\ucf1c\uc57c \ud558\ub294 \uac83",date:"2010-11-10",categories:["blahblah"],tags:["\uac1c\uc778","\uc0ac\ub78c","\uc870\uc9c1"]},unlisted:!1,prevItem:{title:"\ud2b8\uc704\ud130\uc5d0 \uc4f4 \uae00 on 2010-11-20",permalink:"/2010/11/20/wp/2010-11-20-\ud2b8\uc704\ud130\uc5d0-\uc4f4-\uae00-on-"},nextItem:{title:"\uc0b6\uacfc \uc77c\uc758 \uc870\ud654\ub97c \uc704\ud55c 16\uac00\uc9c0 Ground Rule",permalink:"/2010/10/09/wp/\uc0b6\uacfc-\uc77c\uc758-\uc870\ud654\ub97c-\uc704\ud55c-16\uac00\uc9c0-ground-rule"}},c={authorsImageUrls:[]},a=[];function d(n){const e={p:"p",strong:"strong",...(0,s.R)(),...n.components};return(0,r.jsxs)(r.Fragment,{children:[(0,r.jsx)(e.p,{children:"2\ub144 \uc804\uc5d0 \ub3d9\uae30 \ud558\ub098\uac00 \ud1f4\uc0ac\ud55c\ub2e4\uae30\uc5d0 \uc774\ubcc4\ud558\ub294 \uc790\ub9ac\ub97c \uc870\ucd10\ud558\uac8c \ub9cc\ub4e4\uc5c8\ub358 \uc801\uc774 \uc788\ub2e4. \uadf8 \uce5c\uad6c\uac00 \ud68c\uc0ac\ub97c \ub5a0\ub098\ub294 \uc774\uc720\ub294 \uc5ec\ub7ec\uac00\uc9c0\uac00 \uc788\uc5c8\uc9c0\ub9cc \ubb34\ucc99 \ud798\ub4e4\uc5b4 \ud588\ub294\ub370 \ub204\uad6c\ub098 \uadf8\ub807\ub4ef\uc774 \uac00\uc7a5 \ud070 \uc774\uc720\ub294 \uc870\uc9c1\uacfc \uc0ac\ub78c\uc5d0 \ub300\ud55c \uc5b4\ub824\uc6c0\uc774\uc5c8\ub358 \uac78\ub85c \uae30\uc5b5\ub41c\ub2e4. \ub098 \uc5ed\uc2dc \uc870\uc9c1\uacfc \uc0ac\ub78c\ub54c\ubb38\uc5d0 \uc5b4\ub835\uace0 \ud798\ub4e4\uc5c8\uace0 \uc9c0\uae08\uae4c\uc9c0\ub3c4 \uc5b4\ub835\ub2e4\uace0 \uc0dd\uac01\ud558\ub294 \ubd80\ubd84\uc774\uc9c0\ub9cc \ub0a0 \uc544\uc9c1 \uc774\uacf3\uc5d0 \ubd99\uc7a1\uc544\ub450\uace0 \uc788\ub294 \uc774\uc720 \uc5ed\uc2dc \uc870\uc9c1\uc774\ub77c\ub294\uac74 \ucc38 \uc544\uc774\ub7ec\ub2c8\ud55c \uc77c\uc774\ub2e4."}),"\n",(0,r.jsx)(e.p,{children:"\ub0b4\uac00 \uc870\uc9c1\uacfc \uc0ac\ub78c\uc5d0\uac8c \uc798 \uc801\uc751\ud558\uace0 \uc788\ub294\uc9c0 \uc544\ub2cc\uc9c0\uc758 \uc5ec\ubd80\ub97c \ub5a0\ub098\uc11c \ub09c \uc870\uc9c1 \uc790\uccb4\uc5d0 \ub300\ud55c \uad00\uc2ec\uc774 \ub9ce\uc740 \ud3b8\uc774\ub2e4. \uc18c\uaddc\ubaa8 \uc778\uc6d0\uc774 \ubaa8\uc778 \uc870\uc9c1\uc774 \uc5ec\ub7ff \ubaa8\uc5ec \uac70\ub300 \uae30\uc5c5\uc744 \ud615\uc131\ud558\uace0 \uc6b4\uc601\ub418\uace0 \uc788\ub294 \uac83 \uc790\uccb4\uac00 \uc2e0\uae30\ud558\uae30\ub3c4 \ud558\uc9c0\ub9cc, \uc774 \uacf3 \uc544\ub2c8\uba74 \uc774 \uc815\ub3c4 \uaddc\ubaa8\uc758 \uc870\uc9c1\uc744 \ubc30\uc6b8 \uc218 \uc788\ub294 \uae30\ud68c\uac00 \ubcc4\ub85c \uc5c6\uc744 \uac83 \uac19\ub2e4\ub294 \uc0dd\uac01\uc5d0 \uac00\ub2a5\ud55c \ub9ce\uc740 \uac83\uc744 \uad00\ucc30\ud558\uace0 \ubc30\uc6b8 \uc0dd\uac01\uc774\ub2e4. \ud63c\uc790 \ud639\uc740 \uc18c\uaddc\ubaa8\ub85c \uc6c0\uc9c1\uc774\ub294 \uac83\uc740 \uc5b8\uc81c\ub098 \uac00\ub2a5\ud558\ub2e4\ub294\uac8c \ub0b4 \uc0dd\uac01 \uc911 \ud558\ub098\uc774\uae30\ub3c4 \ud558\uace0."}),"\n",(0,r.jsx)(e.p,{children:'\uc5b8\uc820\uac00 \uadf8\ub7f0 \uae00\uc744 \ubcf8 \uc801 \uc788\ub2e4. \ub204\uad70\uac00\uac00 \uc2eb\uc744 \ub54c \uadf8 \uc0ac\ub78c\uc744 \uc790\uc138\ud788 \uad00\ucc30\ud574 \ubcf4\uba74 \uadf8 \ubaa8\uc2b5\uc774 \ub098\uc77c \uac00\ub2a5\uc131\uc774 \ub192\ub2e4\ub294 \ub0b4\uc6a9\uc774\uc5c8\ub294\ub370 \uc0c1\ub2f9\ud788 \uacf5\uac10\ud588\uc5c8\uace0, \uadf8\ub7f0 \uacbd\uc6b0\ub4e4\uc744 \ub2ee\uc544\uc11c\ub294 \uc548\ub418\ub294 \ubaa8\uc2b5\uc73c\ub85c \uae30\ub85d\ud574 \ub450\uba74 \uc88b\uc744 \uac83\uc774\ub77c\ub294 \ub0b4\uc6a9\ub3c4 \uc88b\uc558\ub2e4. \uc694\uc998 \ubcf5\uc7a1\ud558\uace0 \ubd88\ud3b8\ud55c \ub9c8\uc74c\uc744 \uac16\uac8c \ub418\uae38\ub798 \uac00\ub9cc\ud788 \uc0dd\uac01\uc744 \uc815\ub9ac\ud574\ubcf4\ub2c8 \uac00\uc7a5 \ud070 \uc6d0\uc778\uc740 \uc870\uc9c1 \ub0b4\uc5d0\uc11c\uc758 \uc0ac\ub78c\uad00\uacc4 \ub54c\ubb38\uc774\ub77c\ub294 \uacb0\ub860\uc744 \ub0b4\ub9b4 \uc218 \uc788\uc5c8\ub2e4. \ud608\uc561\ud615\ubcc4 \uc131\uaca9\uc5d0 \ub300\ud55c \uc7ac\ubbf8\uc788\ub294 \uae00\ub4e4\uc744 \uc77d\uc5b4\ubcf4\uba74 \ub0b4\uac00 \ub9cc\ub098\uac8c \ub418\ub294 \uc0ac\ub78c\ub4e4\uc740 \ub124\uac00\uc9c0 \uc720\ud615 \uc911 \ud558\ub098\uc5d0 \ubd88\uacfc\ud558\uace0, \uacbd\uc6b0\ub97c \ub298\ub824\ubcf4\ub824\uace0 MBTI \uac80\uc0ac\uae4c\uc9c0 \ub3d9\uc6d0\ud574\ubd24\uc790 \uc5f4 \uc5ec\uc12f \uc911 \ud558\ub098\uac00 \ub418\uc5b4\uc57c \ud558\ub294\ub370 \ubaa8\ub450 \ub2e4\ub974\ub2e4\ub294\uac74 \ucc38 \uc774\uc0c1\ud55c \uc77c\uc774 \uc544\ub2d0 \uc218 \uc5c6\ub2e4. \uad00\uacc4\ud558\uac8c \ub418\ub294 \uc218\ub9ce\uc740 \uc0ac\ub78c\ub4e4 \uc911 \ub098\uc640 \uc798 \ub9de\ub294\ub2e4\uba74 \ubb38\uc81c\uac00 \uc5c6\uaca0\uc9c0\ub9cc \ub098\ub97c, \ub610 \uad6c\uc131\uc6d0\ub4e4 \uc804\uccb4\ub97c \ubd88\ud3b8\ud558\uac8c \ub9cc\ub4e0\ub2e4\uba74 \uadf8\uac74 \ub0b4\uac00 \ub2ee\uc544\uc11c\ub294 \uc548\ub420 \ubaa8\uc2b5\uc77c\uac70\ub2e4. \uadf8\ub798\uc11c \ucd5c\uadfc \uad00\ucc30\ud558\uac8c\ub41c \uc548 \uc88b\uc740 \ubaa8\uc2b5\ub4e4\uc744 \ub098\uc5f4\ud574 \ubcfc\uae4c \ud55c\ub2e4. \ub2e4\ub978 \uc0ac\ub78c\ub4e4\uc774 "\ub2f9\uc2e0\ub3c4 \uc5ed\uc2dc \uadf8\ub798\uc694" \ub77c\uace0 \ub9d0\ud560 \uc218\ub3c4 \uc788\uc9c0\ub9cc \ub0b4 \uc2a4\uc2a4\ub85c\uc5d0\uac8c \uacbd\uace0\ud558\uace0 \ubc18\uc131\ud558\ub294 \uae30\ud68c\ub85c \uc0bc\uc744 \uc218 \uc788\uc744\uac70\ub780 \uc0dd\uac01\uc774\ub2e4. \uc81c\ubaa9\uc5d0 "\uc717 \uc0ac\ub78c, \uc544\ub7ab \uc0ac\ub78c" \uc774\ub77c\ub294 \ub2e8\uc5b4\ub97c \uc0ac\uc6a9\ud574\uc11c \ubcf4\uc218\uc801\uc774\ub780 \ub290\ub08c\uc774 \ub4e4\uc9c0\ub9cc \ub531\ud788 \ub2e4\ub974\uac8c \ud45c\ud604\ud560 \ub9d0\uc774 \uc5c6\ub2e4\ub294\uac8c \uc544\uc27d\ub2e4. \uadf8\ub0e5 "\uc870\uc9c1\ub0b4\uc5d0\uc11c \uc0ac\ub78c\ub4e4\uc744 \ubd88\ud3b8\ud558\uac8c \ub9cc\ub4dc\ub294 \ud589\ub3d9\ub4e4" \uc774\ub77c\uace0 \ud560\uae4c? \uadf8\ub798. \ubd80\uc81c\ub85c \uc815\ud574\ub450\uc790.'}),"\n",(0,r.jsx)(e.p,{children:(0,r.jsx)(e.strong,{children:"[\uc717 \uc0ac\ub78c\uc774 \ud574\uc11c\ub294 \uc548 \ub420 \ud589\ub3d9]"})}),"\n",(0,r.jsx)(e.p,{children:(0,r.jsx)(e.strong,{children:"1. \uc544\ub7ab \uc0ac\ub78c \uc55e\uc5d0\uc11c \uadf8 \uc0ac\ub78c\ubcf4\ub2e4 \uc704\uc5d0 \uc788\ub294 \uc778\ubb3c\ub4e4\uc5d0 \ub300\ud55c \ud5d8\ub2f4"})}),"\n",(0,r.jsx)(e.p,{children:"- \uc870\uc9c1\uc744 \uc57d\ud654\uc2dc\ud0ac \uc218 \uc788\ub2e4. \uc544\ub7ab \uc0ac\ub78c\uc740 \ud5d8\ub2f4\uc758 \ub300\uc0c1\uc774 \ub418\ub294 \uc790\uc2e0\uc758 \ubc14\ub85c \uc704\uc5d0 \uc788\ub294 \uc0ac\ub78c\uc744 \uae54\ubcf4\uac70\ub098 \ubb34\uc2dc\ud558\uac8c \ub420 \uac00\ub2a5\uc131\uc774 \ub192\uace0 \uc911\uac04\uc5d0 \uc704\uce58\ud55c \uadf8 \uc0ac\ub78c\uc774 \ud754\ub4e4\ub9ac\uac70\ub098 \ubb34\ub108\uc9c0\uba74 \uc5f0\uacb0\uace0\ub9ac\uac00 \uc0ac\ub77c\uc9c0\ub294 \uac83\uacfc \uac19\uae30 \ub54c\ubb38\uc774\ub2e4. \uc9e7\uc9c0\ub9cc \uacbd\ud5d8\uc0c1 \uc911\uac04\uc774 \ube44\uc5b4\uc788\ub294 \uc870\uc9c1\uc740 \uad00\ub9ac\uc790\ub3c4 \ud798\ub4e4\uace0 \uc544\ub7ab \uc0ac\ub78c\ub4e4\ub3c4 \ud798\ub4e4\uc5b4\ud55c\ub2e4. \ub098\uc758 \uacbd\uc6b0\uc5d4 \uc81c\uc77c \uc704\uc5d0 \uc788\ub294 \uad00\ub9ac\uc790\uac00 \ub0b4 \uc0c1\uae09\uc790\uc5d0 \ub300\ud55c \uc548\uc88b\uc740 \uc774\uc57c\uae30\ub97c \uaebc\ub0b4\uba74 \ub0b4\uc6a9\uc774 \uc0ac\uc2e4\uc77c\uc9c0\ub77c\ub3c4 \uad00\ub9ac\uc790\uc758 \uc790\uc9c8\uc744 \uba3c\uc800 \uc758\uc2ec\ud558\uac8c \ub41c\ub2e4. \uc65c\ub0d0\ud558\uba74 \uc6b0\ub9ac\ub294 \uac1c\uc778 \ud50c\ub808\uc774\ub97c \ud558\ub294\uac8c \uc544\ub2c8\ub77c \uc870\uc9c1 \ub2e8\uc704\ub85c \uc6c0\uc9c1\uc5ec\uc57c \ud558\ub2c8\uae4c. \uc815 \ub9c8\uc74c\uc5d0 \ub4e4\uc9c0 \uc54a\ub294\ub2e4\uba74 \ub530\ub85c \ubd88\ub7ec\ub0b4\uc11c \ub300\ud654\ub85c \ud480\uc5b4\uc57c\ud560 \uc77c\uc774\ub77c\uace0 \ubcf8\ub2e4. \uc544\ub7ab \uc0ac\ub78c\ub4e4 \uc55e\uc5d0\uc120 \uc911\uac04\uc5d0 \uc704\uce58\ud55c \uc778\ubb3c\uc5d0 \ub300\ud55c \uc804\ud3ed\uc801\uc778 \uc2e0\ub8b0\ub97c \ubcf4\ub0b4\ub294 \uac83\ucc98\ub7fc '\uc5f0\uae30'\ub77c\ub3c4 \ud560 \ud544\uc694\uac00 \uc788\ub2e4."}),"\n",(0,r.jsx)(e.p,{children:(0,r.jsx)(e.strong,{children:"2. \ub2a5\ub825\uc5d0 \ub300\ud55c \ud3c4\ud558"})}),"\n",(0,r.jsx)(e.p,{children:"- \ub204\uad6c\ub098 \uc7a5\uae30\uac00 \uc788\uace0 \ud2b9\uc9d5\uc774 \uc788\ub2e4. \ub0b4\uac00 \uc885\uc0ac\ud558\uace0 \uc788\ub294 \uc5c5\uc885\uc740 \uae30\uc220, \ud2b8\ub80c\ub4dc\uc5d0 \ub300\ud55c \ubcc0\ud654\uac00 \uadf9\uc2ec\ud55c \ud3b8\uc778\ub370\ub2e4\uac00 \ubcf8\uc778\uc774 \ud574\uc57c\ud558\ub294 \uc5c5\ubb34\uc758 \uc804\ud658\ub3c4 \ubb34\ucc99 \ube60\ub974\uac8c \uc774\ub8e8\uc5b4\uc9c4\ub2e4. \uadf8\ub7f0 \uc678\ubd80 \ud658\uacbd\uc758 \ubcc0\ud654\uac00 \ubc18\uac11\uace0 \ube60\ub974\uac8c \uc801\uc751\ud558\ub294 \uc0ac\ub78c\uc774 \uc788\ub294 \ubc18\uba74, \uadf8\ub807\uc9c0 \ubabb\ud55c \uc0ac\ub78c\ub4e4\ub3c4 \ubd84\uba85 \uc874\uc7ac\ud55c\ub2e4. \uadf8\ub798\uc11c \uc717 \uc0ac\ub78c\uc774\ub77c\uba74 \uadf8\ub807\uc9c0 \ubabb\ud55c \uc0ac\ub78c\ub4e4\uc744 \ubc30\ub824\ud558\uace0 \ub3c5\ub824\ud560 \ud544\uc694\uac00 \uc788\ub2e4\uace0 \ubcf8\ub2e4. \uc0ac\uc2e4 \uadf8 \uc0ac\ub78c\uc774 \ubcc0\ud654\uc5d0 \ub300\ud574 \uc870\uae08 \ub290\ub9b0 \uac83\uc77c \ubfd0\uc774\uc9c0 \ub2a5\ub825\uc774 \uc5c6\ub294 \uac83\uc740 \uc544\ub2c8\ub2e4. \ub09c \uadf8\ub7f0 \uacbd\uc6b0\ub97c \uc54c\uace0 \uc788\uace0, \ub300\uc0c1\uc774 \ub418\ub294 \uadf8 \ubd84\uc740 \uc5b4\ub5a4 \uba74\uc5d0\uc11c\ub294 \ucda9\ubd84\ud788 \uc874\uacbd\ubc1b\uc744 \uc218 \uc788\ub294 \ub2a5\ub825\uc744 \uac00\uc9c0\uace0 \uc788\ub2e4. \uc9c0\uae08 \ub2f9\uc7a5 \uc5b4\ub514\uc120\uac00 \uc694\uad6c\ub418\ub294 \uc77c\uc744 \ube68\ub9ac \ucc98\ub9ac\ud558\uc9c0 \ubabb\ud55c\ub2e4\ub294 \uc774\uc720\ub85c \uadf8 \uc0ac\ub78c\uc774 \ubb34\ub2a5\ub825\ud55c \uac83\ucc98\ub7fc \ub300\uc6b0\ud558\ub294 \uac83\uc740 \uc808\ub300 \uc717\uc0ac\ub78c\uc774 \ud574\uc11c\ub294 \uc548 \ub420 \ud589\ub3d9\uc774\ub2e4. \uacbd\ud5d8\uc0c1 \uadf8 \ub290\ub9bf\ud55c \uc0ac\ub78c\uc758 \ub178\ud558\uc6b0\uac00 \uc808\uc2e4\ud788 \ud544\uc694\ud574\uc9c8 \ub54c\uac00 \uc62c \uac8c \ubd84\uba85\ud55c\ub370 \uadf8 \ub54c \uac00\uc11c\ub294 \uc544\uc26c\uc6b4 \uc18c\ub9ac \ud560\uac74\uac00? \ubaa8\uc591 \ube60\uc9c0\uac8c \uadf8\ub7f0 \uc0c1\ud669\uc744 \ub9cc\ub4e4\ub824\uace0 \ud558\uc9c0 \ub9d0\uc790. \ubaa8\ub4e0 \uac1c\uc778\uc774 \uc7a5\uc810\uc744 \uac00\uc9c0\uace0 \uc788\uc73c\ub2c8 \uadf8\ub7f0 \uac83\ub4e4\uc744 \ucda9\ubd84\ud788 \uc0b4\ub9b4 \uc218 \uc788\ub294 \ud658\uacbd\uc744 \ub9cc\ub4e4\uc5b4\uc8fc\ub294\uac8c \uc5ec\ub7ec\uac00\uc9c0\ub85c \uc88b\uc740 \ubc29\ubc95\uc774\ub2e4. \uc870\uc9c1\uc758 \ubd84\uc704\uae30\ub3c4 \uc88b\uc544\uc9c0\uace0, \uadf8\ub798\uc11c \uc77c\ub3c4 \ub354 \ube68\ub9ac \uc9c4\ud589\ub420\uac70\ub77c\uace0 \ubbff\ub294\ub2e4. \uc5c5\ubb34 \uc131\uacfc\uc5d0 \ub300\ud55c \ubcf4\ub78c\ubfd0 \uc544\ub2c8\ub77c \uc870\uc9c1 \uad6c\uc131\uc6d0\uc744 \ubc30\ub824\ud588\ub2e4\ub294 \uc0ac\uc2e4, \uadf8 \ub9c8\uc74c\uc758 \ubfcc\ub4ef\ud568 \ub9cc\uc73c\ub85c\ub3c4 \ud589\ubcf5\ud574\uc9c0\uc9c0 \uc54a\uc744\uae4c? \uc544! \ud55c\uac00\uc9c0 \ube60\uc84c\ub294\ub370 \ub0b4\uac00 \ud68c\uc0ac\uc5d0\uc11c \ubcf8 \ubaa8\ub4e0 \uc0ac\ub78c\ub4e4\uc740 \ubaa8\ub450 \ub2a5\ub825\uc744 \uac00\uc9c0\uace0 \uc788\ub2e4. \uadf8 \ub2a5\ub825\uc758 \uc885\ub958\uac00 \ub2e4\ub97c \ubfd0\uc774\uc9c0. \uc6b0\ub9ac \ud68c\uc0ac\ub294 \ub2a5\ub825\uc5c6\ub294 \uc0ac\ub78c\uc740 \uc4f0\uc9c0 \uc54a\ub294\ub2e4."}),"\n",(0,r.jsx)(e.p,{children:(0,r.jsx)(e.strong,{children:"3. \uac15\uc555\uc744 \ud1b5\ud55c \uc5c5\ubb34\uc9c4\ud589"})}),"\n",(0,r.jsx)(e.p,{children:'- \uac15\uc555\uc801\uc778 \uc5c5\ubb34\uc9c0\uc2dc. \uc77c\ubd80\ubd84\uc740 \uc88b\uc740 \uc810\uc774 \uc788\ub2e4. \uadf8\ub7f0 \uc9c0\uc2dc\ub97c \ubc1b\uc544\uc11c \uc774\ud589\ud558\ub294 \uc544\ub7ab\uc0ac\ub78c\uc758 \uacbd\uc6b0 \uc2ec\uc801\uc778 \ubd80\ub2f4\uc744 \uac16\uac8c \ub418\uc9c0\ub9cc \uc624\ud788\ub824 \uae34\uc7a5\ud560 \uc218 \uc788\uc5b4\uc11c \uc2e4\uc218\ub97c \uc904\uc774\uac70\ub098 \uc5c5\ubb34\ub2a5\ub825\uc744 \ube60\ub974\uac8c \ud5a5\uc0c1\uc2dc\ud0ac \uc218 \uc788\uae30 \ub54c\ubb38\uc774\ub2e4. \ud558\uc9c0\ub9cc \uac1c\uc778\uc758 \ud2b9\uc131\uc744 \uace0\ub824\ud558\uc9c0 \uc54a\uace0 \ub3d9\uc77c\ud55c \uac15\ub3c4\ub85c \uc555\ubc15\ud558\uba74 \uc5b4\ub5a4 \uac1c\uc778\uc5d0\uac8c\ub294 \uc624\ud788\ub824 \ud574\uac00 \ub41c\ub2e4\uace0 \ubcf8\ub2e4. \uc758\uc695\uc744 \ub5a8\uc5b4\ub728\ub9ac\uac70\ub098, \uc2a4\ud2b8\ub808\uc2a4\ub85c \uc778\ud574 \uc5c5\ubb34 \uc774\uc678\uc758 \ubd80\ubd84\uc5d0 \uc545\uc601\ud5a5\uc744 \uc904 \uc218 \uc788\uae30 \ub54c\ubb38\uc778\ub370 \uadf8 \uc545\uc601\ud5a5\uc740 \uacb0\uad6d \ub3cc\uace0 \ub3cc\uc544 \uc5c5\ubb34\uc5d0 \uc601\ud5a5\uc744 \ub07c\uce58\uac8c \ub41c\ub2e4. \ub300\ubd80\ubd84\uc758 \uacbd\uc6b0\uc5d4 \uadf8\ub807\ub2e4. \uc870\uc9c1 \uad6c\uc131\uc6d0\ub4e4 \ubaa8\ub450\ub294 \uc77c\uc744 \uc798 \ud558\uace0 \uc2f6\uc740 \uc695\uc2ec\uc744 \uac00\uc9c0\uace0 \uc788\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4. \uc77c\uc815\uc744 \uc544\ub7ab \uc0ac\ub78c \uc2a4\uc2a4\ub85c\uc5d0\uac8c \uacc4\ud68d\ud558\uace0 \uc791\uc131\ud558\uac8c \ub9cc\ub4e4\uba74 \uadf8 \uc544\ub7ab \uc0ac\ub78c\uc740 \ubcf8\uc778\uc758 \uc5ec\ub7ec\uac00\uc9c0 \ud658\uacbd\uc744 \uace0\ub824\ud558\uac8c \ub420 \uac83\uc774\uace0 \ud639\uc2dc \uadf8\ub807\uac8c \ub9cc\ub4e4\uc5b4\uc9c4 \uacc4\ud68d\uc774 \uc804\uccb4 \uc77c\uc815\uc0c1 \uc704\ud5d8\ud558\ub2e4 \ud310\ub2e8\ub420 \uacbd\uc6b0\uc5d0\ub294 \ud568\uaed8 \uc758\ub17c\ud574\uc11c \uc801\ub2f9\ud55c \uc120\uc744 \uc7a1\uc73c\uba74 \ub41c\ub2e4. \ucda9\ubd84\ud55c \uc0c1\ud669 \uc124\uba85, \uc608\ub97c \ub4e4\uc5b4 "\ud504\ub85c\uc81d\ud2b8 \uc804\uccb4 \uc77c\uc815\uc774 \uc774\ub7ec\uc774\ub7ec\ud558\ub2c8 \ub124\uac00 \uc9e0 \uc774 \uc77c\uc815\uc774\ub77c\uba74 \uc774\ub807\uac8c \ub420 \uc218\ub3c4 \uc788\ub2e8\ub2e4. \uadf8\ub7ec\ub2c8 \ub124\uac00 \uc774\ub807\uac8c \uc774\ub807\uac8c \ud574\ubcf4\ub294\uac74 \uc5b4\ub5a8\uae4c? \ubd80\ub2f4\uc2a4\ub7fd\ub2e4\uba74 \ub2e4\ub978 \ubc29\ubc95\uc744 \uac15\uad6c\ud574\ubcf4\uc790. \uc194\uc9c1\ud558\uac8c \uc598\uae30\ud574\uc8fc\ub834." \uc815\ub3c4\uc758 \uc9e7\uc740 \ub300\ud654\ub97c \ub098\ub204\ub294 \uac83 \ub9cc\uc73c\ub85c\ub3c4 \uc544\ub7ab \uc0ac\ub78c\uc5d0\uac8c \ucc45\uc784\uac10\uacfc \uae34\uc7a5\uc744 \uc720\ub3c4\ud560 \uc218 \uc788\uace0, \ubc30\ub824\ud560 \uc218 \uc788\ub2e4. \uadf8\ub9ac\uace0 \uc694\uc998\uc740 \uac15\uc555\uc73c\ub85c \uc0b0\ucd9c\ubb3c\uc744 \ub9ce\uc774 \ubf51\uc544\ub0b4 \uc131\uacf5\ud560 \uc218 \uc788\ub294 \uc2dc\ub300\uac00 \uc544\ub2c8\ub2e4. \ube68\ub9ac \uc775\ud788\uace0 \uc77c\ud558\ub294 \uac83, \uc88b\uc544 \ubcf4\uc77c \uc218 \uc788\uc9c0\ub9cc \uacb0\uad6d\uc740 \ubcbd\uc744 \ub118\uc9c0\ub294 \ubabb\ud560\uac70\ub77c\uace0 \ubcf8\ub2e4. \ud68c\uc0ac\uc5d0\uc11c \ub2a5\ub825 \uc778\uc815 \ubc1b\uc73c\uba74\uc11c \ubbf8\uce5c\ub4ef\uc774 \uc77c\ub9cc \ud558\ub358 \ub3d9\uae30\uac00 \ucd9c\uc7a5\uc73c\ub85c \uc560\ud50c \ubcf8\uc0ac\uc5d0\uc11c \uba87 \uac1c\uc6d4 \uc0dd\ud65c\ud558\uace0 \ub098\uc11c \ud68c\uc0ac\ub97c \uadf8\ub9cc\ub450\ub294 \uac78 \ub09c \ubd24\ub2e4.'}),"\n",(0,r.jsx)(e.p,{children:(0,r.jsx)(e.strong,{children:"[\uc544\ub7ab \uc0ac\ub78c\uc774 \ud574\uc11c\ub294 \uc548 \ub420 \ud589\ub3d9]"})}),"\n",(0,r.jsx)(e.p,{children:(0,r.jsx)(e.strong,{children:"1. \uc548\ub41c\ub2e4, \ud560 \uc218 \uc5c6\ub2e4, \ud558\uae30 \uc2eb\ub2e4"})}),"\n",(0,r.jsx)(e.p,{children:'- \uc5b4\ub5a4 \uc77c\uc774 \uc548\ub420 \uac83 \uac19\ub2e4\ub294 \uc0dd\uac01\uc774 \ub4dc\ub294 \uacbd\uc6b0\uac00 \uc788\ub2e4. \ubd88\uac00\ub2a5\ud558\ub2e4\uace0 \ud310\ub2e8\ub420 \uc218\ub3c4 \uc788\ub2e4. \ud558\uc9c0\ub9cc \ub09c \uacbd\ud5d8\ud55c \uc5b4\ub5a4 \uacbd\uc6b0\ub77c\ub3c4 \uadf8\ub7f0\uac74 \ubcf8 \uc801\uc774 \uc5c6\ub2e4. \ub610 \uadf8\ub807\uac8c \ubbff\uace0 \uc788\uae30\ub3c4 \ud558\ub2e4. \uc815 \uc548\ub41c\ub2e4\uace0 \uc0dd\uac01\ub418\uba74 \uadfc\uac70\ub97c \uc0c1\uc138\ud558\uac8c \uc124\uba85\ud560 \ud544\uc694\uac00 \uc788\ub2e4. \uc544\ub2c8\uba74 \uc544\uc608 \ub2e4\ub978 \ubc29\uc548\uc744 \uc81c\uc2dc\ud558\ub294 \uac83\ub3c4 \uc88b\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4. \uc608\ub97c \ub4e4\uc5b4, "\ub9d0\uc500\ud558\uc2e0 \uc774 \ubd80\ubd84\uc740 \uadf8\ub807\uac8c \uc9c4\ud589\ub420 \uacbd\uc6b0 \uc774\ub7f0\uc800\ub7f0 \ubb38\uc81c\uac00 \uc788\uae30 \ub54c\ubb38\uc5d0 \ud574\uacb0\uc774 \uc27d\uc9c0 \uc54a\uc744 \uc218 \uc788\uc73c\ub2c8 \uc544\uc608 \uc774\ub807\uac8c \uc800\ub807\uac8c \ud558\ub294\uac74 \uc5b4\ub5a8\uae4c\uc694?" \ub77c\ub294 \uc2dd\uc774\ub77c\uba74 \uc88b\uc744 \uac83 \uac19\ub2e4. \ubd88\uac00\ub2a5\ud558\ub2e4\uace0 \ub9d0\ud558\uace0 \uc2f6\uc744 \ub54c\uc5d0\ub294 \ub2e8\uc5b4\uc120\ud0dd\uc774\ub77c\ub3c4 \uc798 \ud558\uc790. "\uc27d\uc9c0 \uc54a\uc544\uc11c \uc77c\uc815\uc5d0 \ub9de\ucd94\uae30\uac00 \uc5b4\ub835\ub2e4" \uc815\ub3c4\uba74 \ub418\uc9c0 \uc54a\uc744\uae4c? \uc65c \uc774\ub7f0 \ub9d0\uc744 \ud558\uc9c0 \uc54a\ub294\uac8c \uc911\uc694\ud558\ub0d0\uba74 \uc704\uc5d0\uc11c\ub294 \ucda9\ubd84\ud788 \uc0dd\uac01\ud574\uc11c \uc9c0\uc2dc\ud55c \uc0ac\ud56d\uc778\ub370 \uc548\ub41c\ub2e4\ub294\uac70\ub2c8 \uc717 \uc0ac\ub78c\uc744 \ubc14\ubcf4\ub85c \ub9cc\ub4dc\ub294 \ud589\uc704\uc774\uae30\ub3c4 \ud558\uace0, \ub300\ubd80\ubd84\uc740 \uadf8 \uc0ac\ub78c\uc758 \uc758\uc9c0\uc640 \uc758\uc695\uc744 \uc758\uc2ec\ud558\uac8c \ub9cc\ub4dc\ub294 \ub9d0\uc774\uae30 \ub54c\ubb38\uc774\ub2e4.'}),"\n",(0,r.jsx)(e.p,{children:(0,r.jsx)(e.strong,{children:"2. \uc717 \uc0ac\ub78c\uc758 \uc131\uacfc \uae4e\uc544\ub0b4\ub9ac\uae30"})}),"\n",(0,r.jsx)(e.p,{children:"- \uc774\ub7f0 \uacbd\uc6b0\ub294 \uac70\uc758 \uc5c6\ub2e4\uace0 \uc0dd\uac01\ub418\uc9c0\ub9cc \ubd88\ud589\ud558\uac8c\ub3c4 \uacbd\ud5d8\ud55c \uc801\uc774 \uc788\ub2e4. \uc2e4\uc81c\ub85c \uc717 \uc0ac\ub78c\uc774 \ud574\uacb0\ud55c \ubb38\uc81c\uac00 \ubcc4 \uac83 \uc544\ub2c8\uc5c8\uace0 \ub300\ub2e8\ud55c \uc77c\uc778 \uac83\ucc98\ub7fc \ubcf8\uc778\uc774 \uacf5\uce58\uc0ac\ud588\ub358 \uac83\ub3c4 \uc544\ub2cc\ub370 \uc544\ub7ab \uc0ac\ub78c\uc774 \ub07c\uc5b4\ub4e4\uc5b4 \uadf8\uac78 \uc9c4\uc9dc \uc544\ubb34\uac83\ub3c4 \uc544\ub2cc \uc77c\uc774\ub77c\ub294 \uc2dd\uc73c\ub85c \ubc1b\uc544\uce58\ub294 \uacbd\uc6b0. \ub300\ucc45\uc774 \uc5c6\ub2e4."}),"\n",(0,r.jsx)(e.p,{children:(0,r.jsx)(e.strong,{children:"3. Assumption"})}),"\n",(0,r.jsx)(e.p,{children:'- \uc608\uc804\uc5d0 \uc88b\uc544\ud558\ub358 \ubbf8\uad6d \ubd84\uc5d0\uac8c \ubc30\uc6e0\ub358 \ub0b4\uc6a9. \uc544\ub7ab \uc0ac\ub78c, \uc717 \uc0ac\ub78c \ub530\uc9c8 \uac83 \uc5c6\uc774 \ubaa8\ub450 \uacf5\ud1b5\ub418\ub294 \ub0b4\uc6a9\uc774\uae30\ub3c4 \ud558\uc9c0\ub9cc \uc0c1\ub300\uc801\uc73c\ub85c \uacbd\ud5d8\uc774 \uc801\uc740 \uc544\ub7ab \uc0ac\ub78c\ub4e4\uc774 \uc798 \uc0ac\uc6a9\ud558\ub294 \uae30\uc220\uc774\ub2e4. \ub098 \uc5ed\uc2dc \uc758\uc695\ub9cc \uc55e\uc124 \ub54c \ub9ce\uc774 \uc0ac\uc6a9\ud558\uae30\ub3c4 \ud588\uace0. \ubaa8\ub4e0\uac8c \uba85\ud655\ud558\uc9c0 \uc54a\uc740 \uc548\uac1c\uac00 \uc794\ub729 \ub080 \uac83 \uac19\uc740 \uc0c1\ud669\uc778\ub370 \uc790\uc2e0\uc5d0\uac8c \uacb0\uc815\uad8c\uc740 \uc5c6\uc744 \ub54c, \uadf8 \ub54c \uc774\ub7f0\uc2dd\uc73c\ub85c \ub9d0\uc744 \ud558\uac8c \ub41c\ub2e4. "\uc774\ub7f0 \uacbd\uc6b0\uc5d0\ub294 \uc774\ub807\uac8c \ud558\uba74 \ub420\uac70\uace0, \uc800\ub7f0 \uacbd\uc6b0\uc5d4 \uc800\ub807\uac8c \ud558\uba74 \ub41c\ub2e4. \uc774\ub7f0 \uacbd\uc6b0\uc5d0 \uc774\ub807\uac8c \ud588\uc744 \uacbd\uc6b0\uc5d4 \ubb50\uac00 \ubd88\ud655\uc2e4\ud55c \uc0c1\ud0dc\uc774\uc9c0\ub9cc \uadf8 \ubd88\ud655\uc2e4\ud55c\uac8c \uc774\ub7f0\uac70\ub77c\uba74 \uc774\ub807\uac8c \uc774\ub807\uac8c, \uc800\ub7f0\uac70\ub77c\uba74 \uc800\ub807\uac8c \uc800\ub807\uac8c." \uc801\ub2f9\ud55c \ucd94\uc815\uacfc \uac00\uc815\uc740 \ub9ac\uc2a4\ud06c\ub97c \ubbf8\ub9ac \uc0dd\uac01\ud574 \ubcf8\ub2e4\ub294 \uc810\uc5d0\uc11c \uc88b\uac8c \uc791\uc6a9\ud560 \uc218\ub3c4 \uc788\uc9c0\ub9cc \ub300\ubd80\ubd84\uc758 \uacbd\uc6b0\uc5d4 \ub610 \uc2ec\ud55c \ucd94\uc815\uc740 \uc624\ud788\ub824 \ud63c\ub780\ub9cc \uac00\uc911\uc2dc\ud0a8\ub2e4. \uadf8\ub9ac\uace0 \uc4f8\ubaa8\uc5c6\uac8c \ub420 \uac00\ub2a5\uc131\ub3c4 \ub192\uc544\uc11c, \uc790\uc2e0\uc758 \uc758\uc695\uc774 \ud558\ub298\ub85c \uce58\uc19f\uace0 \uc788\uc74c\uc744 \uc790\ub791\ud558\ub294 \ud589\uc704\uc5d0 \ubd88\uacfc\ud558\uac8c \ub418\uace0 \uacb0\uad6d\uc740 \uc5d0\ub108\uc9c0 \ub0ad\ube44\ub2e4. \ub9d0\ub85c \uac00\uc815\ud558\uace0 \ucd94\uc815, \ucd94\uce21\ud558\uc9c0 \ub9d0\uace0 \ubcc0\uc218\uac00 \ub9ce\uc740 \uacbd\uc6b0\uc5d4 \uadf8 \ubcc0\uc218\ub97c \ud558\ub098\uc529 \uace0\uc815\uc2dc\ud0ac \uc218 \uc788\ub294 \ubc29\uc548\uc744 \ub9c8\ub828\ud574\uc11c \ud589\ub3d9\ud558\ub294\uac8c \ud6e8\uc52c \ud6a8\uc728\uc801\uc774\ub77c\uace0 \uc0dd\uac01\ud55c\ub2e4.'}),"\n",(0,r.jsx)(e.p,{children:"\ud68c\uc0ac\uc640 \ud68c\uc0ac\ub97c \uad6c\uc131\ud558\uace0 \uc788\ub294 \uc18c\uaddc\ubaa8 \uc870\uc9c1\ub4e4\uc740 \ubaa8\ub450 \ubaa9\ud45c\ub97c \uac00\uc9c0\uace0 \uc788\ub2e4. \uadf8\ub9ac\uace0 \uadf8 \ubaa9\ud45c\ub97c \ub2ec\uc131\ud558\uae30 \uc704\ud574 \ub2ec\ub824\uac00\ub294 \uac83\ucc98\ub7fc \ubcf4\uc778\ub2e4. \ud558\uc9c0\ub9cc \uad6c\uc11d\uad6c\uc11d \ub4e4\uc5ec\ub2e4\ubcf4\uba74 \uadf8 \ubaa9\ud45c\ub77c\ub294\uac8c \uacf5\uc720\ub418\uc9c0 \ubabb\ud558\ub294 \uacbd\uc6b0\ub3c4 \ub9ce\uace0, \uc65c \uadf8\ub7f0 \uac83\ub4e4\uc744 \ud574\uc57c\ud558\ub294\uc9c0 \uc774\uc720\uc870\ucc28 \ubd88\ubd84\uba85\ud558\ub2e4\uace0 \uc0dd\uac01\ud558\ub294 \uacbd\uc6b0\ub3c4 \uc788\uae30 \ub54c\ubb38\uc5d0 \ub3d9\uae30\ubd80\uc5ec\uac00 \ub418\uc9c8 \uc54a\uace0, \uacb0\uad6d\uc740 \ub530\ub85c \ub530\ub85c \uc6c0\uc9c1\uc774\uac8c \ub41c\ub2e4. \uc5b4\ub290\uc815\ub3c4 \uaddc\ubaa8\uac00 \ucee4\uc9c0\uba74 \uc5b4\uca54 \uc218 \uc5c6\uc774 \ubc1c\uc0dd\ud558\ub294 \ubb38\uc81c\uc774\uae30\ub3c4 \ud55c \uac83 \uac19\uc9c0\ub9cc."}),"\n",(0,r.jsx)(e.p,{children:"\uc870\uc9c1\uc740 \uc0b4\uc544\uc788\ub294 \uc0dd\uba85\uccb4 \uac19\uc544\uc11c \uc6b0\ub9ac \ubab8\uc744 \ub2e4\ub8e8\ub294 \uac83\ucc98\ub7fc \uad6c\uc11d\uad6c\uc11d \uad00\uc2ec\uc744 \uac16\uace0 \uc5b4\ub8e8\ub9cc\uc838 \uc918\uc57c \ud560 \ud544\uc694\uac00 \uc788\ub2e4. \uc870\uc9c1\uc740 \uc5b8\ub73b \ub2e8\uc21c\ud558\uace0 \ud558\ub098\ub85c \ub418\uc5b4\uc788\ub294 \uac83\ucc98\ub7fc \ub290\uaef4\uc9c0\uae30\ub3c4 \ud558\uc9c0\ub9cc \uadf8 \uad6c\uc131\uc6d0\ub4e4\uc740 \ub2e8\uc21c\ud788 \uba87\uac00\uc9c0, \uba87\uc2ed\uac00\uc9c0\ub85c \uad6c\ubd84\ub420 \uc218 \uc5c6\ub294 \uac1c\uc131\uc774\ub780 \uac78 \uac00\uc9c0\uace0 \uc788\ub294 \uc874\uc7ac\ub4e4\uc774\uae30 \ub54c\ubb38\uc5d0 \ubaa8\ub450 \ube44\uc2b7\ud55c \uc0ac\ub78c\ub4e4\uc774\ub77c\uace0 \ucc29\uac01\ud558\uc9c0 \ub9d0\uace0, \uc11c\ub85c \uc874\uc911\ud560 \uc218 \uc788\ub294 \ubb38\ud654\uac00 \ud544\uc694\ud558\ub2e4\uace0 \uc0dd\uac01\ud55c\ub2e4. \ub108\ubb34 \uc9c4\ubd80\ud558\uc9c0\ub9cc \uadf8 \uae30\ubcf8\uc774 \uc81c\ub300\ub85c \uc9c0\ucf1c\uc9c0\uc9c0 \uc54a\ub294 \uc0ac\ud68c\uc5d0 \uc6b0\ub9ac\uac00 \uc0b4\uace0 \uc788\uae30 \ub54c\ubb38\uc5d0 \ud56d\uc0c1 \uc544\uc26c\uc6b4 \ubd80\ubd84."})]})}function p(n={}){const{wrapper:e}={...(0,s.R)(),...n.components};return e?(0,r.jsx)(e,{...n,children:(0,r.jsx)(d,{...n})}):d(n)}},28453:(n,e,t)=>{t.d(e,{R:()=>i,x:()=>o});var r=t(96540);const s={},l=r.createContext(s);function i(n){const e=r.useContext(l);return r.useMemo((function(){return"function"==typeof n?n(e):{...e,...n}}),[e,n])}function o(n){let e;return e=n.disableParentContext?"function"==typeof n.components?n.components(s):n.components||s:i(n.components),r.createElement(l.Provider,{value:e},n.children)}}}]);